sp_widget.client_script:
function(spUtil) {
  /* widget controller */
  var c = this;
	c.addRecord = function(){
		var resp = grecaptcha.getResponse();
		if (!resp){
			spUtil.addErrorMessage("Please check the recaptcha.");
		}
		else {
			c.server.get({
				action: "add_record",
				requested_for: c.data.requested_for,
				short_description: c.data.short_description,
				google: resp
			}).then(function(response){
				if (response.data.recaptcha.success){
					c.data = {};
					grecaptcha.reset();
				}
				else{
					spUtil.addErrorMessage("You're a bot!");
				}
			});
		}


	}
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	if (input && input.action === "add_record"){
		//var r = new sn_ws.RESTMessageV2('GoogleRecaptcha', 'get');
		var r = new sn_ws.RESTMessageV2('x_58872_needit.GoogleRecaptcha', 'Default GET');
		r.setStringParameter('resp', input.google);
		var response = r.execute();
		data.recaptcha = JSON.parse(response.getBody());
		if (data.recaptcha.success){
			var gr = new GlideRecord('x_58872_needit_needit');
			gr.newRecord();

			gr.short_description = input.short_description;

			var gd = new GlideDate(); 
			gd.setValue('2020-10-01');
			gr.u_when_needed = gd;
			gr.u_what_needed = 'Legal 1';
			gr.u_request_type = 'Legal';

			var gr_user = new GlideRecord('sys_user');
			gr_user.addQuery('name', input.requested_for);
			gr_user.query();

			if (gr_user.next()){
				gr.u_requested_for = gr_user.sys_id + '';
				gr.u_requested_for_email = gr_user.email;
				gr.insert();
			}
		}
	}


})();
